    private void btncalcularActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btncalcularActionPerformed
    int cantidad;
    cantidad=Integer.parseInt(txtcantidad.getText());
        
       Clases.Producto objeto1= new Clases.Producto();
        
        objeto1.setprecio(cantidad);
        double precio=objeto1.obtenerprecio();
        double descuento=objeto1.obtenerdescuento();
        double totalapagar=objeto1.total();
        
        txtarea.setText("el precio es: "+precio+ "\n ");
        txtarea.append("El descuento es: " + descuento+"\n ");
        txtarea.append("el total a pagar es: "+ totalapagar);
                                              

    }//GEN-LAST:event_btncalcularActionPerformed

